import styled from "styled-components";

export const Container = styled.div`
    height: 134px;
    background-color: #fff;
    border-radius: 8px;
    max-width: 327px;
    position: relative;
`

export const Id = styled.h4`
    position: absolute;
    top: 24px;
    left: 24px;
`

export const Name = styled.p`
    color: #858BB2;
    position: absolute;
    top: 24px;
    right: 24px;
`

export const DateInfo = styled.p`
    color: #858BB2;
    position: absolute;
    top: 63px;
    left: 24px;
`

export const Status = styled.h4`
    color: #33D69F;
`
